class Zrole extends CI_Controller
{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Zrole_model');
    } 

    /*
     * Listing of zroles
     */
    function index()
    {
        $data['zroles'] = $this->Zrole_model->get_all_zroles();

        $this->load->view('zrole/index',$data);
    }

    /*
     * Adding a new zrole
     */
    function add()
    {
        $this->load->library('form_validation');
        if(isset($_POST) && count($_POST) > 0)     
        {

            $params = array(
                //agregado
                'zrolcodigo' =>$this->input->post('zrolcodigo'),
                //
				'zroldescripcion' => $this->input->post('zroldescripcion'),
            );
            
            $zrole_id = $this->Zrole_model->add_zrole($params);
            redirect('zrole/index');
        }
        else
        {

			$this->load->model('Permiso_model');
			$data['all_permisos'] = $this->Permiso_model->get_all_permisos();
                
            $this->load->view('zrole/add',$data);
        }
    }  

    /*
     * Editing a zrole
     */
    function edit($zrolcodigo)
    {
        $this->load->library('form_validation');
        // check if the zrole exists before trying to edit it
        $zrole = $this->Zrole_model->get_zrole($zrolcodigo);
        
        if(isset($zrole['zrolcodigo']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'zroldescripcion' => $this->input->post('zroldescripcion'),
                );

                $this->Zrole_model->update_zrole($zrolcodigo,$params);            
                redirect('zrole/index');
            }
            else
            {   
                $data['zrole'] = $this->Zrole_model->get_zrole($zrolcodigo);
                //cargo permisos de tabla cruzada
				$this->load->model('Permxrol_model');
				$data['all_permisos'] = $this->Permxrol_model->get_perm_x_rol($zrolcodigo);
                //Cargo caracteristicas del permiso
                $this->load->model('Permiso_model');
                $res=[];
                foreach ($data['all_permisos'] as $pxr){
                    $res[$pxr['zpermcodigo']] = $this->Permiso_model->get_permiso($pxr['zpermcodigo']);
                }

                $data['permisos_detalle'] = $res;

                $this->load->view('zrole/edit',$data);
            }
        }
        else
            show_error('The zrole you are trying to edit does not exist.');
    } 

    /*
     * Deleting zrole
     */
    function remove($zrolcodigo)
    {
        $zrole = $this->Zrole_model->get_zrole($zrolcodigo);

        // check if the zrole exists before trying to delete it
        if(isset($zrole['zrolcodigo']))
        {
            $this->Zrole_model->delete_zrole($zrolcodigo);
            redirect('zrole/index');
        }
        else
            show_error('The zrole you are trying to delete does not exist.');
    }
    
}
