class Zusuario extends CI_Controller
{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Zusuario_model');
    } 

    /*
     * Listing of zusuarios
     */
    function index()
    {
        $data['zusuarios'] = $this->Zusuario_model->get_all_zusuarios();

        $this->load->view('zusuario/index',$data);
    }

    /*
     * Adding a new zusuario
     */
    function add()
    {
        $this->load->library('form_validation');
        //Agregado
        $this->db->select_max('zusrcodigo');
        $result= $this->db->get('zusuarios')->row_array();
        //
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
                //agregado
                'zusrcodigo' =>$result['zusrcodigo']+1,
                //
				'zrolcodigo' => $this->input->post('zrolcodigo'),
				'per_codigo' => $this->input->post('per_codigo'),
				'zusrnombre' => $this->input->post('zusrnombre'),
				'zusrclave' => $this->input->post('zusrclave'),
            );
            
            $zusuario_id = $this->Zusuario_model->add_zusuario($params);
            redirect('zusuario/index');
        }
        else
        {

			$this->load->model('Zrole_model');
			$data['all_zroles'] = $this->Zrole_model->get_all_zroles();

			$this->load->model('Profesor_model');
			$data['all_profesor'] = $this->Profesor_model->get_all_profesor();
                
            $this->load->view('zusuario/add',$data);
        }
    }  

    /*
     * Editing a zusuario
     */
    function edit($zusrcodigo)
    {
        $this->load->library('form_validation');
        // check if the zusuario exists before trying to edit it
        $zusuario = $this->Zusuario_model->get_zusuario($zusrcodigo);
        
        if(isset($zusuario['zusrcodigo']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'zrolcodigo' => $this->input->post('zrolcodigo'),
					'per_codigo' => $this->input->post('per_codigo'),
					'zusrnombre' => $this->input->post('zusrnombre'),
					'zusrclave' => $this->input->post('zusrclave'),
                );

                $this->Zusuario_model->update_zusuario($zusrcodigo,$params);            
                redirect('zusuario/index');
            }
            else
            {   
                $data['zusuario'] = $this->Zusuario_model->get_zusuario($zusrcodigo);
    
				$this->load->model('Zrole_model');
				$data['all_zroles'] = $this->Zrole_model->get_all_zroles();

				$this->load->model('Profesor_model');
				$data['all_profesor'] = $this->Profesor_model->get_all_profesor();

                $this->load->view('zusuario/edit',$data);
            }
        }
        else
            show_error('The zusuario you are trying to edit does not exist.');
    } 

    /*
     * Deleting zusuario
     */
    function remove($zusrcodigo)
    {
        $zusuario = $this->Zusuario_model->get_zusuario($zusrcodigo);

        // check if the zusuario exists before trying to delete it
        if(isset($zusuario['zusrcodigo']))
        {
            $this->Zusuario_model->delete_zusuario($zusrcodigo);
            redirect('zusuario/index');
        }
        else
            show_error('The zusuario you are trying to delete does not exist.');
    }
    
}
